// Good code: Secure deserialization with proper validation and filtering
import java.io.*;
import java.io.ObjectInputStream;
import java.io.ObjectStreamClass;

public class DeserializationSecure {
    public static void main(String[] args) {
        try {
            // Use a custom ObjectInputStream with a whitelist of allowed classes
            ObjectInputStream ois = new ObjectInputStream(new FileInputStream("user_data.ser")) {
                @Override
                protected Class<?> resolveClass(ObjectStreamClass desc) throws IOException, ClassNotFoundException {
                    // Only allow specific, safe classes to be deserialized
                    String name = desc.getName();
                    if (!name.equals("com.example.SafeData")) {
                        throw new InvalidClassException("Unauthorized class: " + name);
                    }
                    return super.resolveClass(desc);
                }
            };

            Object obj = ois.readObject(); // Now safe due to class filtering
            ois.close();
            System.out.println("Deserialized object: " + obj);
        } catch (IOException | ClassNotFoundException e) {
            e.printStackTrace();
        }
    }
}
